mapper.select:
select s.id, s.name, s.content, s.created_at as createdAt, s.updated_at as updatedAt from psticks s

    user_id = #{userId}

        and name like concat('%',#{name},'%')


        and content like concat('%',#{content},'%')


        and s.created_at >= #{startDate}


        and s.created_at <= #{endDate}
