mapper.select:
SELECT id, user_id AS userId, topic_id AS topicId, title, content, post_type AS postType, status, like_count AS likeCount, comment_count AS commentCount, share_count AS shareCount, view_count AS viewCount, publish_location AS publishLocation, latitude, longitude, created_at AS createdAt, updated_at AS updatedAt FROM posts
AND post_type = #{type}
AND status = #{status}
ORDER BY view_count DESC, created_at DESC
ORDER BY created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM posts


        AND post_type = #{type}


        AND status = #{status}

SELECT id,
       created_at AS createdAt
FROM posts


        AND post_type = #{type}


        AND status = #{status}


ORDER BY created_at DESC
LIMIT #{limit}
