mapper.select:
select s.id, s.content, s.created_at as createdAt, s.updated_at as updatedAt from sticks s

    user_id = #{userId}

        and content like concat('%',#{content},'%')


        and s.created_at >= #{startDate}


        and s.created_at <= #{endDate}

mapper.update:
update sticks
content = #{content}, updated_at = CURRENT_TIMESTAMP
where id = #{tileId}
